import React from "react";
import { useRecoilState, useRecoilValue } from "recoil";
import { categoryState, todoSelector, todoState } from "../atoms";
import CreateTodo from "./CreateTodo";
import Todo from "./Todo";

const ToDoList = () => {
  // const todos = useRecoilValue(todoState); // 리팩토링하며 modifier 함수 필요없어졌기 때문에 useRecoilValue 사용
  const [todos, doings, dones] = useRecoilValue(todoSelector); // 배열 풀어주기
  const [category, setCategory] = useRecoilState(categoryState);

  const onInput = (event: React.FormEvent<HTMLSelectElement>) => {
    setCategory(event.currentTarget.value);
  };

  return (
    <div>
      <h1>TO DOs</h1>
      <select onInput={onInput} value={category}>
        <option value="TO_DO">To Do</option>
        <option value="DOING">Doing</option>
        <option value="DONE">Done</option>
      </select>
      <CreateTodo />
      {category === "TO_DO"
        ? todos.map((todo) => <Todo key={todo.id} {...todo} />)
        : category === "DOING"
        ? doings.map((doing) => <Todo key={doing.id} {...doing} />)
        : dones.map((done) => <Todo key={done.id} {...done} />)}
    </div>
  );
};

export default ToDoList;
