// fetch 함수 따로 만들어주기
// api 관련 작업은 component 와 분리되어있는 것이 좋다
export const coinsFetcher = () => {
  // async - await 대신 then promie 문법 사용
  return fetch("https://api.coinpaprika.com/v1/coins").then((res) =>
    res.json()
  );
};
