import { Link } from "react-router-dom";
import styled from "styled-components";

const Container = styled.div`
  padding: 0px 20px;
`;
const Header = styled.header`
  height: 10vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const CoinsList = styled.ul``;
const Coin = styled.li`
  background-color: white;
  color: ${props => props.theme.bgColor};
  margin-bottom: 10px;
  padding: 20px;
  border-radius: 15px;
  &:hover {
    a {
      color: ${props => props.theme.accentColor}
    }
  }
`;
const Title = styled.h1`
  font-size: 48px;
  color: ${props => props.theme.accentColor};
`;

const coins = [
  {"id":"leo-leo-token","name":"LEO Token","symbol":"LEO","rank":26,"is_new":false,"is_active":true,"type":"token"},
  {"id":"near-near-protocol","name":"Near Protocol","symbol":"NEAR","rank":27,"is_new":false,"is_active":true,"type":"coin"},
  {"id":"okb-okb","name":"OKB","symbol":"OKB","rank":31,"is_new":false,"is_active":true,"type":"token"},
  {"id":"mana-decentraland","name":"Decentraland","symbol":"MANA","rank":32,"is_new":false,"is_active":true,"type":"token"},
  {"id":"hbar-hedera-hashgraph","name":"Hedera Hashgraph","symbol":"HBAR","rank":34,"is_new":false,"is_active":true,"type":"coin"},
  {"id":"ftm-fantom","name":"Fantom","symbol":"FTM","rank":35,"is_new":false,"is_active":true,"type":"coin"},
  {"id":"vet-vechain","name":"VeChain","symbol":"VET","rank":42,"is_new":false,"is_active":true,"type":"coin"},
  {"id":"axs-axie-infinity","name":"Axie Infinity","symbol":"AXS","rank":43,"is_new":false,"is_active":true,"type":"token"},
  {"id":"xmr-monero","name":"Monero","symbol":"XMR","rank":45,"is_new":false,"is_active":true,"type":"coin"},
  {"id":"theta-theta-token","name":"THETA","symbol":"THETA","rank":46,"is_new":false,"is_active":true,"type":"coin"},
  {"id":"hnt-helium","name":"Helium","symbol":"HNT","rank":47,"is_new":false,"is_active":true,"type":"coin"}
];

const Coins = () => {
  return (
    <Container>
      <Header>
        <Title>코인</Title>
      </Header>
      <CoinsList>
        {coins.map((coin) => {
          return (
            <Coin key={coin.id}>
              <Link to={`/${coin.id}`}>{coin.symbol} &rarr;</Link>
            </Coin>
          )
        })}
      </CoinsList>
    </Container>
  )
};

export default Coins;
