import { useQuery } from "react-query";
import { useParams } from "react-router";
import { coinHistoryFetcher } from "../api";
import ApexChart from "react-apexcharts";

interface HistoricalDataInterface {
  time_open: string;
  time_close: string;
  open: number;
  high: number;
  low: number;
  close: number;
  volume: number;
  market_cap: number;
}

const Chart = () => {
  const { coinId } = useParams<{ coinId: string }>();
  // console.log(coinId);
  const { isLoading, data } = useQuery<HistoricalDataInterface[]>(
    ["ohlcv", coinId],
    () => coinHistoryFetcher(coinId ? coinId : "")
  );
  return (
    <div>{isLoading ? "Loading chart..." : <ApexChart type="line" />}</div>
  );
};

export default Chart;
