import { atom, selector } from "recoil";

export const minuteState = atom({
  key: "minutes",
  default: 0,
});
export const hourSelector = selector({
  key: "hours",
  // get function 을 통해 selector 안에서 atom 에 접근할 수 있게 됨
  get: ({ get }) => {
    const minutes = get(minuteState);
    return minutes / 60;
  },
});
